import React, { useState, useEffect } from "react";
import "./Navbar.css";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  // aggiorna se la finestra cambia dimensione
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const toggleMenu = () => setMenuOpen(!menuOpen);

  return (
    <nav className="navbar">
      <div className="navbar-container">

        {/* Logo invariato */}
        <div className="navbar-logo">
          <a href="/">
            <img src="/images/logo.png" alt="Seriana Cobras" />
          </a>
        </div>

        {/* Bottone hamburger */}
        <button
          className="navbar-toggle"
          onClick={toggleMenu}
          aria-label="Apri menu"
        >
          ‚ò∞
        </button>

        {/* Se desktop ‚Üí sezioni classiche */}
        {!isMobile && (
          <ul className="navbar-links">
            <li><a href="/">Home</a></li>
            <li><a href="/roster">Roster</a></li>
            <li><a href="/sponsor">Sponsor</a></li>
            <li><a href="/store">Store</a></li>
            <li><a href="/contacts">Contatti</a></li>
          </ul>
        )}

        {/* Se mobile ‚Üí pannello griglia */}
        {isMobile && menuOpen && (
          <div className="mobile-grid">
            <div className="grid-item"><a href="/">üè† Home</a></div>
            <div className="grid-item"><a href="/roster">üë• Roster</a></div>
            <div className="grid-item"><a href="/sponsor">üí∞ Sponsor</a></div>
            <div className="grid-item"><a href="/store">üõí Store</a></div>
            <div className="grid-item"><a href="/contacts">üìû Contatti</a></div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
